import { Router } from "express";
const router = Router()
import { appAuth } from "../../utils/is-auth";
import getInfo from "../../utils/get-info";
import getDatabyId from "../../controllers/system/getDatabyId";
import path from "path";
import puppeteer from "puppeteer";

const logo = "data:image/jpeg;base64,";

router.get('/registro', appAuth, function(request, response, next){
    const i = getInfo(request)
    response.render('veh/envios', i);
    })

router.get("/registro/:id", appAuth, function(request, response){
	const i = getInfo(request)
	i.id = request.params.id
	const envios = getDatabyId(request, 'envios.vw_envios', 'codenvio', request.params.id)
	const detalle = getDatabyId(request, 'envios.detalle', 'codenvio', request.params.id)

	Promise.all([envios, detalle])
		.then(function(values){
			i.encabezado = values[0].recordset[0] || {};
			i.detalle = values[1].recordset || {};

			// Procesar las fechas en el encabezado
			if (i.encabezado.datfecha)
				i.encabezado.datfecha = new Date(i.encabezado.datfecha).toISOString().split('T')[0]  // Formato: DD/MM/YYYY

			response.render("veh/enviosbyid", i)
		}).catch(function(err){
		console.log(err);
		response.render("system/error-500", err)
	})

})

router.get("/reporte/:id", appAuth, function(request, response){
	const i = getInfo(request)
	i.id = request.params.id
	const ventas = getDatabyId(request, 'ventas.vw_ventas', 'codventa', request.params.id)
	const poa = getDatabyId(request, 'ventas.vw_rel_core_poa', 'codventa', request.params.id)
	const detalle = getDatabyId(request, 'ventas.vw_detalle', 'codventa', request.params.id)

	Promise.all([ventas, poa, detalle])
		.then(function(values){
			i.encabezado = values[0].recordset[0] || {};
			i.poa = values[1].recordset || {};
			i.detalle = values[2].recordset || {};

			// Procesar las fechas en el encabezado
			if (i.encabezado.datfchventa)
				i.encabezado.datfchventa = new Date(i.encabezado.datfchventa).toISOString().split('T')[0]  // Formato: DD/MM/YYYY

			response.render("veh/ventasReporte", i)
		}).catch(function(err){
		console.log(err);
		response.render("system/error-500", err)
	})

})

router.get("/pdf/:id", appAuth, async function (request, response) {
	try {
		const i = getInfo(request);
		i.id = request.params.id;

		// Ajuste: solo 2 consultas (encabezado + detalle)
		const envio = getDatabyId(request, "envios.core", "codenvio", i.id);      // o envios.vw_envios
		const detalle = getDatabyId(request, "envios.detalle", "codenvio", i.id); // o envios.vw_detalle

		const [rsEnvio, rsDetalle] = await Promise.all([envio, detalle]);

		const encabezado = rsEnvio.recordset?.[0] || {};
		const items = rsDetalle.recordset || [];

		const path_file_pdf = path.join(
			__dirname,
			"../../../public/pdf/",
			`envios-${i.id}.pdf`
		);

		const browser = await puppeteer.launch({
			args: ["--no-sandbox", "--disable-setuid-sandbox"],
		});

		const page = await browser.newPage();

		// HTML completo (dos copias: 1 con data, 1 en blanco como el ejemplo)
		const html = renderEnvioHTML({
			encabezado,
			items,
			logo, // reutiliza su variable logo (idealmente data-uri o ruta accesible)
		});

		await page.emulateMediaType("print");
		await page.setContent(html, { waitUntil: "networkidle0" });
		await page.setCacheEnabled(false);

		await page.pdf({
			path: path_file_pdf,
			format: "legal",
			landscape: false,
			printBackground: true,
			margin: {
				top: "10mm",
				bottom: "10mm",
				left: "8mm",
				right: "8mm",
			},
		});

		await browser.close();

		response.sendFile(path_file_pdf, function (err) {
			if (err) {
				console.error("Error al enviar el archivo:", err);
				response.status(500).send("Error al cargar el archivo.");
			}
		});
	} catch (err) {
		console.error(err);
		response.status(500).send("Error al generar PDF: " + err.message);
	}
});

/* =========================
   Render HTML (2 copias)
========================= */

function renderEnvioHTML({ encabezado, items, logo }) {
	const fechaLarga = formatDateLong(encabezado.datfecha);
	const fechaPedido = formatDateShort(encabezado.datfpedido);

	// Totales
	const subtotal = items.reduce((a, x) => a + (Number(x.numimporte) || 0), 0);
	const flete = Number(encabezado.numflete) || 0;
	const descuento = Number(encabezado.numdescuento) || 0;
	const total = Number(encabezado.numtotal) || (subtotal + flete - descuento);

	const totalLetras = (encabezado.strtotal || "").toString().toUpperCase();

	const copia1 = renderBloqueEnvio({
		encabezado,
		items,
		logo,
		fechaLarga,
		fechaPedido,
		subtotal,
		flete,
		descuento,
		total,
		totalLetras,
		blankDetalle: false, // con data
	});

	const copia2 = renderBloqueEnvio({
		encabezado,
		items: [],
		logo,
		fechaLarga,
		fechaPedido,
		subtotal: 0,
		flete: 0,
		descuento: 0,
		total: 0,
		totalLetras: "",
		blankDetalle: true, // en blanco (como el ejemplo)
	});

	return `
<!doctype html>
<html lang="es">
<head>
<meta charset="utf-8" />
<title>ENVIO ${encabezado.numdocumento || ""}</title>
<style>
  @page { size: Legal; margin: 0; }
  body { font-family: Arial, Helvetica, sans-serif; margin: 0; padding: 8mm; font-size: 12px; }
  table { border-collapse: collapse; width: 100%; table-layout: fixed; }
  .box { border: 2px solid #111; }
  .b1 { border: 1px solid #111; }
  .b2 { border: 2px solid #111; }
  .gray { background: #d9d9d9; font-weight: bold; }
  .center { text-align: center; }
  .right { text-align: right; }
  .top { vertical-align: top; }
  .mid { vertical-align: middle; }
  .pad4 { padding: 4px; }
  .pad6 { padding: 6px; }
  .titleEnvio { font-style: italic; font-weight: 800; letter-spacing: 1px; }
  .muted { color: #222; }
  .spacer { height: 16mm; }
  .cut { border-top: 2px solid #111; margin: 2mm 0; }
  .small { font-size: 11px; }
  .xs { font-size: 10px; }
  .mono { font-variant-numeric: tabular-nums; }
</style>
</head>
<body>
  ${copia1}
  <div class="cut"></div>
  ${copia2}
</body>
</html>
`;
}

