function renderDetalleRows(items, maxRows = 6, blank = false) {
	const rows = [];

	for (let i = 0; i < maxRows; i++) {
		const it = items[i];

		if (blank || !it) {
			rows.push(`
        <tr>
          <td class="b1 pad4 center">&nbsp;</td>
          <td class="b1 pad4">&nbsp;</td>
          <td class="b1 pad4 center">&nbsp;</td>
          <td class="b1 pad4 right mono">&nbsp;</td>
          <td class="b1 pad4 right mono">&nbsp;</td>
        </tr>
      `);
			continue;
		}

		rows.push(`
      <tr>
        <td class="b1 pad4 center">${safe(it.codproducto ?? it.codlinea ?? "")}</td>
        <td class="b1 pad4">${safe(it.strdescripcion)}</td>
        <td class="b1 pad4 center mono">${num(it.numcantidad)}</td>
        <td class="b1 pad4 right mono">${money(it.numpreciounitario)}</td>
        <td class="b1 pad4 right mono">${money(it.numimporte)}</td>
      </tr>
    `);
	}

	return rows.join("");
}

/* =========================
   Helpers
========================= */

function safe(v) {
	if (v === null || v === undefined) return "";
	return String(v)
		.replaceAll("&", "&amp;")
		.replaceAll("<", "&lt;")
		.replaceAll(">", "&gt;");
}

function money(n) {
	const x = Number(n) || 0;
	return x.toLocaleString("en-US", { minimumFractionDigits: 2, maximumFractionDigits: 2 });
}

function num(n) {
	const x = Number(n);
	if (Number.isNaN(x)) return "";
	return x.toLocaleString("en-US", { maximumFractionDigits: 2 });
}

function formatDateShort(d) {
	if (!d) return "";
	const dt = new Date(d);
	const dd = String(dt.getDate()).padStart(2, "0");
	const mm = String(dt.getMonth() + 1).padStart(2, "0");
	const yy = dt.getFullYear();
	return `${dd}/${mm}/${yy}`;
}

function formatDateLong(d) {
	if (!d) return "";
	const dt = new Date(d);
	const s = new Intl.DateTimeFormat("es-GT", {
		weekday: "long",
		year: "numeric",
		month: "long",
		day: "2-digit",
	}).format(dt);

	// Capitalizar primera letra: "lunes" -> "Lunes"
	return s.charAt(0).toUpperCase() + s.slice(1);
}

